import React from 'react'; 
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class AddProject extends React.Component 
{ 
    state = {
        projName : "",
        description : "",
        startDate : new Date(),
        endDate : new Date()
    }

    setName = (e) => {
        this.setState(
            {
                projName : e.target.value
            }
        );
    }

    setDescription = (e) => {
        this.setState(
            {
                description : e.target.value
            }
        );
    }

    setStartDate = (date) => {
        this.setState(
            {
                startDate : date
            }
        );
    }

    setEndDate = (date) => {
        this.setState(
            {
                endDate : date
            }
        );
    }

    onSubmit = (e) => {
      e.preventDefault();
      /*let xhr = new XMLHttpRequest();
      //Send the proper header information along with the request
      xhr.setRequestHeader('Content-type', 'application/json');
      xhr.open('GET', "https://projectionsazurefunctions.azurewebsites.net/api/AddProject", true);*/
      const url = "http://localhost:7071/api/AddProject";
        fetch(url, {
            method : "POST",
            body:  {
                "ProjectID": "yay",
                "Name": this.state.projName,
                "Description": this.state.description,
                "StartDate": this.state.startDate,
                "EndDate": this.state.endDate
            }
        }).then(
            response => response.text() // .json(), etc.
            // same as function(response) {return response.text();}
        ).then(
            html => console.log(html)
        );
    }

	render(){ 
		return (
            <form onSubmit={this.onSubmit} >
            <React.Fragment>
            <div>
                <h1> Add Project</h1>
                <h3> Enter the details of the project below</h3>
                <table>
                <tr class= "rowht">
                <th><label> Name </label></th>
                <th><input type="text" id="name" onChange={this.SetName}/></th>
                </tr>
                <tr class= "rowht">
                <th><label> Description </label></th>
                <th><input type="text" id="description" onChange={desc => this.SetDescription(desc)}/></th>
                </tr>
                <tr class= "rowht">
                <th><label > Start Date </label></th>
                {/* <th><input type="text" id="duration"/></th> */}
                <th><DatePicker selected={this.state.startDate} onChange={date => this.setStartDate(date)}/></th> 
                </tr>
                <tr class= "rowht">
                <th><label > End Date </label></th>
                {/* <th><input type="text" id="duration"/></th> */}
                <th><DatePicker selected={this.state.endDate} onChange={date => this.setEndDate(date)}/></th> 
                </tr>
                </table>
                <button id="AddProject"> Add </button>
            </div>
            </React.Fragment>
            </form>
        );
	} 
} 

export default AddProject;
