import React from 'react'; 
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class AddProject extends React.Component 
{ 
    state = {
        projName : "StateTesting",
        startDate : new Date("1/1/2021"),
        endDate : new Date() + 60
    }

    setStartDate = (date) => {
        this.setState(
            {
                startDate : date
            }
        );
    }

	render(){ 
		return (
            <React.Fragment>
            <div>
                <h1> Add Project</h1>
                <h3> Enter the details of the project below</h3>
                <table>
                <tr class= "rowht">
                <th><label> Name </label></th>
                <th><input type="text" id="name"/></th>
                </tr>
                <tr class= "rowht">
                <th><label> Description </label></th>
                <th><input type="text" id="description"/></th>
                </tr>
                <tr class= "rowht">
                <th><label > Start Date </label></th>
              {/* <th><input type="text" id="duration"/></th> */}
              <th><DatePicker selected={this.state.startDate} onChange={date => this.setStartDate(date)}/></th> 
                </tr>
                <tr class= "rowht">
                <th><label > End Date </label></th>
              {/* <th><input type="text" id="duration"/></th> */}
              <th><DatePicker selected={this.state.startDate} onChange={date => this.setEndDate(date)}/></th> 
                </tr>
                </table>
                <button id="AddProject"> Add </button>
            </div>
            </React.Fragment>
        );
	} 
} 

export default AddProject;
