import React from 'react';
import './Search.css';

class Search extends React.Component{

    state = {
        projectID: ""
    }

    setProperty = (e) => this.setState({ [e.target.name] : e.target.value });

    onSubmit = async(e) => {
        e.preventDefault();
        const url = "https://projectionsazurefunctions.azurewebsites.net/api/AddProject?code=aRwmurbZKy0napFo3BPImB79fgC3Hx5Ejk4FZGPmUuNWbR2bZIbdZg==?projectName="+this.state.projectID;
        //const url = "http://localhost:7071/api/AddProject";
        fetch(url, {
              method : "GET",
              mode: 'no-cors'
          }).then(response => {
              this.setState({indicatorText: "Added new Project"});
          }).catch(err => {this.setState({indicatorText: "Ran into an error while adding project" + err})});
      }

    render(){
        return(
            <div>
                <h1>Search for the Project ID</h1>
                <table>
                    <tr>
                        <th><label>Project ID</label></th>
                        <th><input type="text" id="ProjectID" name="projectID" value = {this.state.projectID} onChange={this.setProperty}/></th>
                    </tr>
                </table>
                <button id="search">Search</button>
            </div>
        );
    }
}

export default Search;
