import React from 'react'; 
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {v1 as uuid} from "uuid"; 
import "./AddProject.css"
import { TextField, PrimaryButton, Stack, IStackTokens } from 'office-ui-fabric-react';
import { useState } from 'react';


    onSubmit = async(e) => {
      e.preventDefault();
      const url = "https://projectionsazurefunctions.azurewebsites.net/api/AddProject?code=aRwmurbZKy0napFo3BPImB79fgC3Hx5Ejk4FZGPmUuNWbR2bZIbdZg==";
      //const url = "http://localhost:7071/api/AddProject";
      fetch(url, {
            method : "POST",
            headers: {
                'Content-Type': 'application/json'
                // 'Content-Type': 'application/x-www-form-urlencoded',
              },
            body:  JSON.stringify({
                "ProjectID": uuid(),
                "ProjectName": this.state.projName,
                "Description": this.state.description,
                "StartDate": this.state.startDate,
                "EndDate": this.state.endDate,
            })
        }).then(response => {
            this.setState({indicatorText: "Added new Project"});
        }).catch(err => {this.setState({indicatorText: "Ran into an error while adding project" + err})});
    }

	function AddProject()  {
        const projName = useState("");
        const description = useState("");
        const startDate = useState(new Date());
        const endDate = useState(new Date());
        const indicatorText = useState("");


		return (
            <form onSubmit={this.onSubmit} >
            <React.Fragment>
            <div>
                <h1> Add Project</h1>
                <h3> Enter the details of the project below</h3>
                <table>
                <TextField label="Name" Name="projName" value={this.state.projName} onChange={this.setProperty}/>
                <tr class= "rowht">
                <th><label> Name </label></th>
                <th><input name="projName" type="text" id="name" value = {this.state.projName} onChange={this.setProperty}/></th>
                </tr>
                <tr class= "rowht">
                <th><label> Description </label></th>   
                <th><input name="description" type="text" id="description"  value = {this.state.description} onChange={this.setProperty}/></th>
                </tr>
                <tr class= "rowht">
                <th><label > Start Date </label></th>
                <th><DatePicker Name="StartDate" selected={this.state.startDate} onChange={date => this.setStartDate(date)}/></th> 
                </tr>
                <tr class= "rowht">
                <th><label > End Date </label></th>
                <th><DatePicker  Name="EndDate" selected={this.state.endDate} onChange={date => this.setEndDate(date)}/></th> 
                </tr>
                </table>
                <PrimaryButton className="AddProject" text="Primary" onClick={this.onSubmit} allowDisabledFocus />
                {/* <button id="AddProject"> Add </button> */}
                <br/>
                <label className="Indicator">{this.state.indicatorText}</label>
            </div>
            </React.Fragment>
            </form>
        );
	}

export default AddProject;
