import React from 'react'; 
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {v1 as uuid} from "uuid"; 

class AddProject extends React.Component 
{ 
    uuid = require('uuid');
    state = {
        projName : "",
        description : "",
        startDate : new Date(),
        endDate : new Date()
    }

    setProperty = (e) => this.setState({ [e.target.name] : e.target.value });

    setDescription = (e) => {
        this.setState(
            {
                description : e.target.value
            }
        );
    }

    setStartDate = (date) => {
        this.setState(
            {
                startDate : date
            }
        );
    }

    setEndDate = (date) => {
        this.setState(
            {
                endDate : date
            }
        );
    }

    onSubmit = (e) => {
      e.preventDefault();
      /*let xhr = new XMLHttpRequest();
      //Send the proper header information along with the request
      xhr.open('GET', "https://projectionsazurefunctions.azurewebsites.net/api/AddProject", true);*/
      const url = "https://projectionsazurefunctions.azurewebsites.net/api/AddProject";
      fetch(url, {
            method : "POST",
            body:  JSON.stringify({
                "ProjectID": uuid(),
                "ProjectName": this.state.projName,
                "Description": this.state.description,
                "StartDate": this.state.startDate,
                "EndDate": this.state.endDate
            })
        });
    }

	render(){ 
		return (
            <form onSubmit={this.onSubmit} >
            <React.Fragment>
            <div>
                <h1> Add Project</h1>
                <h3> Enter the details of the project below</h3>
                <table>
                <tr class= "rowht">
                <th><label> Name </label></th>
                <th><input name="projName" type="text" id="name" value = {this.state.projName} onChange={this.setProperty}/></th>
                </tr>
                <tr class= "rowht">
                <th><label> Description </label></th>
                <th><input name="description" type="text" id="description"  value = {this.state.description} onChange={this.setProperty}/></th>
                </tr>
                <tr class= "rowht">
                <th><label > Start Date </label></th>
                {/* <th><input type="text" id="duration"/></th> */}
                <th><DatePicker Name="StartDate" selected={this.state.startDate} onChange={date => this.setStartDate(date)}/></th> 
                </tr>
                <tr class= "rowht">
                <th><label > End Date </label></th>
                {/* <th><input type="text" id="duration"/></th> */}
                <th><DatePicker  Name="EndDate" selected={this.state.endDate} onChange={date => this.setEndDate(date)}/></th> 
                </tr>
                </table>
                <button id="AddProject"> Add </button>
            </div>
            </React.Fragment>
            </form>
        );
	} 
} 

export default AddProject;
