import React from 'react'; 
import DatePicker from "react-datepicker";

class AddProject extends React.Component 
{ 
    state = {
        projName : "StateTesting"
    }

	render(){ 
		return (
            <React.Fragment>
            <div>
                <h1> Add Project</h1>
                <h3> Enter the details of the project below</h3>
                <table>
                <tr class= "rowht">
                <th><label> Name </label></th>
                <th><input type="text" id="name"/></th>
                </tr>
                <tr class= "rowht">
                <th><label> Description </label></th>
                <th><input type="text" id="description"/></th>
                </tr>
                <tr class= "rowht">
                <th><label > Start Date </label></th>
              {/* <th><input type="text" id="duration"/></th> */}
                <th><DatePicker selected={new Date()} /></th>
                </tr>
                </table>
                <button id="AddProject"> Add </button>
            </div>
            </React.Fragment>
        );
	} 
} 

export default AddProject;
