import React, {useState, useEffect} from 'react'; 
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {v1 as uuid} from "uuid"; 
import "./AddProject.css"
import { TextField, PrimaryButton, Stack, IStackTokens } from 'office-ui-fabric-react';

	const AddProject = () => {
        const [state, setState] = useState({
            projName : "a",
            description : "b",
            startDate : new Date(),
            endDate : new Date(),
            indicatorText: "b"
          });

          const projName = state.projName;

        function setProperty(e) {
            var name  = e.target.name;
            var val = e.target.value;
            console.log(name + " - " + val + "-" + state.projName);
            //useEffect(() => {
                setState(prevstate => {
                    return {...prevstate,"projName": val };
                });

                console.log(state.projName);
            //}, []);
        } 

        function onSubmit(e) {
            e.preventDefault();
            const url = "https://projectionsazurefunctions.azurewebsites.net/api/AddProject?code=aRwmurbZKy0napFo3BPImB79fgC3Hx5Ejk4FZGPmUuNWbR2bZIbdZg==";
            //const url = "http://localhost:7071/api/AddProject";
            fetch(url, {
                  method : "POST",
                  headers: {
                      'Content-Type': 'application/json'
                      // 'Content-Type': 'application/x-www-form-urlencoded',
                    },
                  body:  JSON.stringify({
                      "ProjectID": uuid(),
                      "ProjectName": state.projName,
                      "Description": state.description,
                      "StartDate": state.startDate,
                      "EndDate": state.endDate,
                  })
              }).then(response => {
                  setState({indicatorText: "Added new Project"});
              }).catch(err => {setState({indicatorText: "Ran into an error while adding project" + err})});
          }


		return (
            <div>
            <form onSubmit={onSubmit} >
            <React.Fragment>
                <h1> Add Project {state.projName}</h1>
                <h3> Enter the details of the project below</h3>
                <table>
                <TextField label="Name" name="projName" value={projName} onChange={setProperty}/>
                <tr class= "rowht">
                <th><label> Name </label></th>
                <th><input name="projName" type="text" id="name" value = {state.projName} onChange={setProperty}/></th>
                </tr>
                <tr class= "rowht">
                <th><label> Description </label></th>   
                <th><input name="description" type="text" id="description"  value = {state.description} onChange={setProperty}/></th>
                </tr>
                <tr class= "rowht">
                <th><label > Start Date </label></th>
                <th><DatePicker Name="StartDate" selected={state.startDate} onChange={setProperty}/></th> 
                </tr>
                <tr class= "rowht">
                <th><label > End Date </label></th>
                <th><DatePicker  Name="EndDate" selected={state.endDate} onChange={setProperty}/></th> 
                </tr>
                </table>
                <PrimaryButton className="AddProject" text="Primary" onClick={onSubmit} allowDisabledFocus />
                {/* <button id="AddProject"> Add </button> */}
                <br/>
                <label className="Indicator">{state.indicatorText}</label>
            </React.Fragment>
            </form>
            </div>
        );
	}

export default AddProject;
