import React, {useState, useEffect} from 'react'; 
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {v1 as uuid} from "uuid"; 
import "./AddProject.css"
import { TextField, PrimaryButton, Stack, IStackTokens } from 'office-ui-fabric-react';

	const AddProject = () => {
        const DayPickerStrings: IDatePickerStrings = {
            months: [
              'January',
              'February',
              'March',
              'April',
              'May',
              'June',
              'July',
              'August',
              'September',
              'October',
              'November',
              'December',
            ],
          
            shortMonths: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
          
            days: ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'],
          
            shortDays: ['S', 'M', 'T', 'W', 'T', 'F', 'S'],
          
            goToToday: 'Go to today',
            prevMonthAriaLabel: 'Go to previous month',
            nextMonthAriaLabel: 'Go to next month',
            prevYearAriaLabel: 'Go to previous year',
            nextYearAriaLabel: 'Go to next year',
            closeButtonAriaLabel: 'Close date picker',
            monthPickerHeaderAriaLabel: '{0}, select to change the year',
            yearPickerHeaderAriaLabel: '{0}, select to change the month',
          };

          const [firstDayOfWeek, setFirstDayOfWeek] = React.useState(DayOfWeek.Sunday);
        const [state, setState] = useState({
            projName : "",
            description : "",
            startDate : new Date(),
            endDate : new Date(),
            indicatorText: ""
          });

        function setProperty(e) {
            var name  = e.target.name;
            var val = e.target.value;
            console.log(name + " - " + val + "-" + state.projName);
            //useEffect(() => {
                setState(prevstate => {
                    return {...prevstate, [name]:  val };
                });

                console.log(state.projName);
            //}, []);
        } 

        function setStartDate(date) {
            console.log(date);
            setState(prevstate => {
                return {...prevstate, "startDate":  date };
            });
        }
    
        function setEndDate(date) {
            console.log(date);
            setState(prevstate => {
                return {...prevstate, "endDate":  date };
            });
        }

        function onSubmit(e) {
            e.preventDefault();
            const url = "https://projectionsazurefunctions.azurewebsites.net/api/AddProject?code=aRwmurbZKy0napFo3BPImB79fgC3Hx5Ejk4FZGPmUuNWbR2bZIbdZg==";
            //const url = "http://localhost:7071/api/AddProject";
            fetch(url, {
                  method : "POST",
                  headers: {
                      'Content-Type': 'application/json'
                      // 'Content-Type': 'application/x-www-form-urlencoded',
                    },
                  body:  JSON.stringify({
                      "ProjectID": uuid(),
                      "ProjectName": state.projName,
                      "Description": state.description,
                      "StartDate": state.startDate,
                      "EndDate": state.endDate,
                  })
              }).then(response => {
                  setState({indicatorText: "Added new Project"});
              }).catch(err => {setState({indicatorText: "Ran into an error while adding project" + err})});
          }


		return (
            <div>
            <form onSubmit={onSubmit} >
            <React.Fragment>
                <h1> Add Project {state.projName}</h1>
                <h3> Enter the details of the project below</h3>
                <TextField label="Name" name="projName" value={state.projName} onChange={setProperty}/>
                <TextField label="Description" name="description" value={state.description} onChange={setProperty}/>
                <DatePicker label="Start Date" placeholder="Select a date..." name="startDate" value={state.startDate} onChange={setStartDate} ariaLabel="Select a date"/>
                <DatePicker label="End Date" placeholder="Select a date..." name="endDate" value={state.endDate} onChange={setEndDate} ariaLabel="Select a date"/>
                <PrimaryButton className="AddProject" text="Primary" onClick={onSubmit} allowDisabledFocus />
                {/* <button id="AddProject"> Add </button> */}
                <br/>
                <label className="Indicator">{state.indicatorText}</label>
            </React.Fragment>
            </form>
            </div>
        );
	}

export default AddProject;
