import React from 'react';
import './Search.css';

class Search extends React.Component{

    state = {
        projectID: "",
        projectName : "",
        description : "",
        startDate : new Date(),
        endDate : new Date(),
    }

    setProperty = (e) => this.setState({ [e.target.name] : e.target.value });

    onSubmit = async(e) => {
        e.preventDefault();
        const url = "https://projectionsazurefunctions.azurewebsites.net/api/Search?code=CbTDUtF5NSctIKh/jP6BmrifZ17wiSyQvqc0COv0G1ybdouglcICWw==&ProjectName="+this.state.projectID;
        //const url = "http://localhost:7071/api/AddProject";
        fetch(url, {
              method : "GET",
              mode: 'no-cors'
          }).then(response => {
              if(response.ok)
              {
                  var project = response.json();
                  this.setState(
                      {
                        projectID: project.projectID,
                        projectName: project.projectName,
                        startDate: project.startDate,
                        endDate: project.endDate,
                        description: project.description
                      }
                  )
              }
          }).catch(err => {alert(err)});
      }

    render(){
        return(
            <div>
                <form onSubmit={this.onSubmit} >
                <h1>Search for the Project ID</h1>
                <table>
                    <tr>
                        <th><label>Project ID</label></th>
                        <th><input type="text" id="ProjectID" name="projectID" value = {this.state.projectID} onChange={this.setProperty}/></th>
                    </tr>
                </table>
                <button id="search">Search</button>
                </form> <br/>
                <table className="projdetails">
                    <tr>
                        <th><label>Project ID</label></th>
                        <th><label>{this.state.projectID}</label></th>
                    </tr>
                    <tr>
                        <th><label>Project Name</label></th>
                        <th><label>{this.state.projectName}</label></th>
                    </tr>
                    <tr>
                        <th><label>Descritption</label></th>
                        <th><label>{this.state.description}</label></th>
                    </tr>
                    <tr>
                        <th><label>Start Date</label></th>
                        <th><label>{this.state.startDate}</label></th>
                    </tr>
                </table>
            </div>
        );
    }
}

export default Search;
